select tablename, policyname, cmd, roles, permissive, qual, with_check from pg_policies where schemaname='public' and tablename in ('clients','appointments','appointment_services','appointment_links') order by tablename, policyname;
select relname as table, relrowsecurity from pg_class c join pg_namespace n on n.oid=c.relnamespace where n.nspname='public' and relname in ('clients','appointments','appointment_services','appointment_links');
